mapper.update:
UPDATE board
   SET read_cnt = NVL(read_cnt,0)+1
 WHERE seq = #{seq}
 AND   reg_id  <>  #{regId}

mapper.select:
SELECT A.*,B.* FROM( SELECT TT1.RNUM AS no, TT1.div, TT1.title, TT1.read_cnt as readCnt, TT1.mod_id as modId, DECODE( TO_CHAR(TT1.mod_dt,'YYYYMMDD'), TO_CHAR(SYSDATE,'YYYYMMDD'), TO_CHAR(TT1.mod_dt,'HH24:MI'),TO_CHAR(TT1.mod_dt,'YYYY/MM/DD') ) AS modDt FROM ( SELECT ROWNUM AS RNUM, T1.* FROM ( SELECT * FROM board --WHERE title(10), contents(20), 30:title+contents
ORDER BY mod_dt DESC ) T1 WHERE ROWNUM <= ( #{pageSize}*(#{pageNo} -1) + #{pageSize} ) )TT1 WHERE tt1.rnum >=( #{pageSize}*(#{pageNo} -1) + 1 ) )A CROSS JOIN( SELECT COUNT(*) totalCnt FROM board
)B
